import{_ as a,o as i,c as e,ag as s}from"./chunks/framework.CoHhbRn3.js";const u=JSON.parse('{"title":"Getting Started","description":"","frontmatter":{},"headers":[],"relativePath":"getting-started.md","filePath":"getting-started.md"}'),l={name:"getting-started.md"};function n(o,t,d,r,h,c){return i(),e("div",null,[...t[0]||(t[0]=[s('<h1 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-label="Permalink to &quot;Getting Started&quot;">​</a></h1><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><p>Install LinNote with a single command:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">curl</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -fsSL</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> https://linnote.app/install.sh</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> |</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;"> bash</span></span></code></pre></div><h3 id="supported-distributions" tabindex="-1">Supported Distributions <a class="header-anchor" href="#supported-distributions" aria-label="Permalink to &quot;Supported Distributions&quot;">​</a></h3><ul><li>Arch Linux / Manjaro</li><li>Debian / Ubuntu / Linux Mint / Pop!_OS</li><li>Fedora</li><li>openSUSE</li></ul><h3 id="requirements" tabindex="-1">Requirements <a class="header-anchor" href="#requirements" aria-label="Permalink to &quot;Requirements&quot;">​</a></h3><ul><li>Qt6 runtime libraries</li><li>KDE Frameworks 6 (KWindowSystem, KGlobalAccel)</li><li>Tesseract (optional, for OCR)</li></ul><blockquote><p><strong>GNOME Users:</strong> For system tray support, install the <a href="https://extensions.gnome.org/extension/615/" target="_blank" rel="noreferrer">AppIndicator extension</a>.</p></blockquote><blockquote><p><strong>Note:</strong> No build tools required - LinNote is distributed as a pre-built binary.</p></blockquote><h2 id="uninstallation" tabindex="-1">Uninstallation <a class="header-anchor" href="#uninstallation" aria-label="Permalink to &quot;Uninstallation&quot;">​</a></h2><p>To completely remove LinNote and all its data:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">curl</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -fsSL</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> https://linnote.app/install.sh</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> |</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;"> bash</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -s</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --uninstall</span></span></code></pre></div><p>Or if you have the script locally:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">./install.sh</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --uninstall</span></span></code></pre></div><p>This removes:</p><ul><li>Binary (<code>/usr/local/bin/linnote</code>)</li><li>Desktop entry</li><li>Icons</li><li>Configuration and data (<code>~/.config/LinNote</code>, <code>~/.local/share/LinNote</code>)</li><li>KDE global shortcuts</li></ul><h2 id="first-launch" tabindex="-1">First Launch <a class="header-anchor" href="#first-launch" aria-label="Permalink to &quot;First Launch&quot;">​</a></h2><ol><li>Open your application menu</li><li>Search for &quot;LinNote&quot;</li><li>Click to launch</li></ol><p>Or run from terminal:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">linnote</span></span></code></pre></div><h2 id="basic-usage" tabindex="-1">Basic Usage <a class="header-anchor" href="#basic-usage" aria-label="Permalink to &quot;Basic Usage&quot;">​</a></h2><ul><li><strong>Type anywhere</strong> - Just start typing</li><li><strong>Keywords</strong> - Type <code>list</code>, <code>code</code>, <code>calc</code> to switch modes</li><li><strong>Menu</strong> - Type <code>/</code> to open command popup</li><li><strong>Save</strong> - Notes auto-save, or press <code>Ctrl+S</code></li></ul><h2 id="mode-switching" tabindex="-1">Mode Switching <a class="header-anchor" href="#mode-switching" aria-label="Permalink to &quot;Mode Switching&quot;">​</a></h2><p>Type the keyword and press Enter:</p><table tabindex="0"><thead><tr><th>Keyword</th><th>Mode</th></tr></thead><tbody><tr><td><code>plain</code></td><td>Plain Text</td></tr><tr><td><code>list</code></td><td>Checklist</td></tr><tr><td><code>code</code></td><td>Code</td></tr><tr><td><code>calc</code></td><td>Calculator</td></tr><tr><td><code>timer</code></td><td>Timer</td></tr><tr><td><code>md</code></td><td>Markdown</td></tr></tbody></table><h2 id="quick-tips" tabindex="-1">Quick Tips <a class="header-anchor" href="#quick-tips" aria-label="Permalink to &quot;Quick Tips&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Action</th><th>How</th></tr></thead><tbody><tr><td>New note</td><td><code>Ctrl+N</code></td></tr><tr><td>Save</td><td><code>Ctrl+S</code></td></tr><tr><td>Search</td><td><code>Ctrl+F</code></td></tr><tr><td>Settings</td><td><code>Ctrl+,</code></td></tr><tr><td>Open menu</td><td>Type <code>/</code></td></tr></tbody></table>',28)])])}const g=a(l,[["render",n]]);export{u as __pageData,g as default};
